import { Link } from 'react-router-dom';
import axios from "axios"

import 'bootstrap/dist/css/bootstrap.min.css';
const Login = () => {

    
    axios.get("http://localhost:300/users").then(
        u
    );

    return (
        <div class="container mt-5">
            <div class="row">
                <div className="col  mt-5">
                    <form action="" method="POST" class="login-email">
                        <h1 >Login</h1>
                        <div class="col form-group mt-3" >
                            <input class="form-control w-" type="email" placeholder="Email" name="email" />
                        </div>
                        <div class="col form-group mt-3" >
                            <input class="form-control" type="password" placeholder="Password" name="password" />
                        </div>
                        <div class="col form-group mt-3" >
                            <a className="btn btn-outline-block btn-primary mb-5" name="submit" >login</a>
                        </div>
                        <p >Don't have an account? <Link to='/'> <a >Register Here</a></Link>.</p>
                    </form>
                </div>
                <div className="col  mt-5">
                    <img src='img/secure.svg' alt='success-image' className="w-100 mt-2" /></div>

            </div>
        </div>

    )
}

export default Login;
